<script>
import { mapGetters, mapActions } from 'vuex'

export default {
  name: 'PageIndex',
  data () {
    return {
      items: []
    }
  },
  mounted () {
    this.items = this.getLinks
  },
  computed: {
    ...mapGetters('main', ['getLinks'])
  },
  methods: {
    ...mapActions('main', ['setLinks']),
    async storeLinks () {
      const inew = await this.setLinks(event.data)
      this.$q.notify({ message: `store links: ${inew} checked...`, color: 'positive', position: 'top' })
    }
  }
}
